import React, { useState } from "react";
import { Form, Field } from "react-final-form";
import { useDispatch, useSelector } from "react-redux";
import { addTodo } from "../../actions";
import { Input } from "../Input";
import { ItemInput } from "../Input";

interface Props {
  placeHolder: string;
  action: (...a: any) => { type: string; payload: string; id?: number };
  actionArguments?: {
    payload: string;
    id: number;
  };
  toggleTitleState?: () => void;
  deleteHendler?: () => any;
}
let state = { data: {} };
const Textinput = ({
  placeHolder,
  action,
  actionArguments,
  toggleTitleState,
  deleteHendler,
}: Props) => {
  const dispatch = useDispatch();
  const todoList = useSelector((store: any) => store.todoList);
  const [inputTitle, setInputTitle] = useState("");
  // state = { data: { title: "...", titleItem: "item" } };

  const handelOnSubmit = (values: any) => {
    console.log(state);
    console.log(values.titleItem);

    // setInputTitle(values.title);
    // setInputTitle("");
    // state = { data: { title: "" } };

    if (actionArguments) {
      state = { data: { title: ".", titleItem: values.titleItem } };
    } else {
      state = { data: { title: ".", titleItem: "." } };
    }

    // actionArguments
    //   ? (state = { data: { title: "obj" } })
    //   : (state = { data: { title: " we" } });

    actionArguments
      ? dispatch(action(values.titleItem, actionArguments.id))
      : dispatch(addTodo(values.title));

    if (toggleTitleState) toggleTitleState();

    if (values.title.trim() === "" && Object.keys(values).length === 0) {
      // if (inputTitle === "") {
      if (deleteHendler) deleteHendler();
      return;
    }

    // Object.keys(values).length === 0
  };

  return (
    <div>
      <Form
        onSubmit={handelOnSubmit}
        initialValues={state.data}
        render={({ form, values }) => (
          <form
            onSubmit={(e: any) => (
              e.preventDefault(e),
              Object.keys(values).length !== 0 && handelOnSubmit(values),
              form.reset()
            )}
          >
            {!actionArguments ? (
              <Field<string>
                name="title"
                component={Input}
                placeholder={actionArguments ? inputTitle : placeHolder}
                onBlur={(e: any) => (
                  e.preventDefault(e),
                  Object.keys(values).length !== 0 && handelOnSubmit(values),
                  form.reset()
                )}
              />
            ) : (
              <Field<string>
                name="titleItem"
                component={ItemInput}
                placeholder={actionArguments ? inputTitle : placeHolder}
                onBlur={(e: any) => (
                  e.preventDefault(e),
                  Object.keys(values).length !== 0 && handelOnSubmit(values),
                  form.reset()
                )}
              />
            )}
          </form>
        )}
      />
    </div>
  );
};

export default Textinput;
