import { strict } from "assert";
import { format } from "path";
import { stringify } from "querystring";
import React, { useCallback } from "react";
import { Form, Field } from "react-final-form";
import { FieldRenderProps } from "react-final-form";
import { useDispatch } from "react-redux";

interface Props {
  placeHolder: string;
  action: (...a: any) => { type: string; payload: string; id?: number };
  actionArguments?: {
    payload: string;
    id: number;
  };
  toggleTitleState?: () => void;
  deleteHendler?: () => any;
}
type PropsInput = FieldRenderProps<string, any>;

const TextInput: React.FC<PropsInput> = ({
  input,
  meta,

  ...rest
}: PropsInput) => <input type="text" {...input} {...rest} autoFocus />;

const Textinput = ({
  placeHolder,
  action,
  actionArguments,
  toggleTitleState,
  deleteHendler,
}: Props) => {
  // const handelOnSubmit = (values: any) => {
  //   // console.log(values);

  // };

  const dispatch = useDispatch();
  let input: any;
  const initialvalu = actionArguments ? actionArguments.payload : "";

  const handelOnSubmit = useCallback((values: any) => {
    console.log(values.title);

    if (toggleTitleState) toggleTitleState();
    if (Object.keys(values).length !== 0) {
      if (deleteHendler) deleteHendler();
      return;
    }
    actionArguments
      ? dispatch(action(values.title, actionArguments.id))
      : dispatch(action(values.title));
  }, []);

  return (
    <div>
      <Form
        onSubmit={handelOnSubmit}
        render={({ form, values }) => (
          <form
            onSubmit={(e: any) => (
              e.preventDefault(e),
              Object.keys(values).length !== 0 && handelOnSubmit(values),
              form.reset(),
              form
            )}
          >
            <Field<string>
              name="title"
              component={TextInput}
              placeholder={placeHolder}
            />
          </form>
        )}
      />
    </div>
  );
};
