// import { ADD_NEWONE_TASK } from "../constants";
import { SHOW_ALL } from "../constants/filter";

const currentFilter = (state = SHOW_ALL, actions: any) => {
  switch (actions.type) {
    case "all":
      return (state = "all");
    case "completed":
      return (state = "completed");
    case "active":
      return (state = "active");
  }
};

export default currentFilter;
