import React from "react";
import { Form, Field } from "react-final-form";
import { useDispatch, useSelector } from "react-redux";
import { addTodo } from "../../actions";
import { Input } from "../Input";

interface Props {
  placeHolder: string;
  action: (...a: any) => { type: string; payload: string; id?: number };
  actionArguments?: {
    payload: string;
    id: number;
  };
  toggleTitleState?: () => void;
  deleteHendler?: () => any;
}

const Textinput = ({
  placeHolder,
  action,
  actionArguments,
  toggleTitleState,
  deleteHendler,
}: Props) => {
  const dispatch = useDispatch();
  const todoList = useSelector((store: any) => store.todoList);

  const handelOnSubmit = (values: any) => {
    actionArguments
      ? dispatch(action(values.title, actionArguments.id))
      : dispatch(addTodo(values.title));
    if (toggleTitleState) toggleTitleState();
    if (!values.title.trim()) {
      if (deleteHendler) deleteHendler();
      return;
    }
    // if (Object.keys(values).length === 0) {
    //   if (deleteHendler) deleteHendler();
    //   return;
    // }
  };

  return (
    <div>
      <Form
        onSubmit={handelOnSubmit}
        render={({ form, values }) => (
          <form
            onSubmit={(e: any) => (
              e.preventDefault(e),
              Object.keys(values).length !== 0 && handelOnSubmit(values),
              form.reset(),
              (values.title = "sss")
            )}
          >
            <Field<string>
              name="title"
              component={Input}
              placeholder={placeHolder}
              onBlur={(e: any) => (
                e.preventDefault(e),
                // Object.keys(values).length !== 0 && handelOnSubmit(values),
                !values.title.trim() && handelOnSubmit(values),
                form.reset()
              )}
            />
          </form>
        )}
      />
    </div>
  );
};

export default Textinput;
