import { strict } from "assert";
import { format } from "path";
import { stringify } from "querystring";
import React from "react";
import { Form, Field } from "react-final-form";
import { FieldRenderProps } from "react-final-form";

interface Props {
  placeHolder: string;
  action: (...a: any) => { type: string; payload: string; id?: number };
  actionArguments?: {
    payload: string;
    id: number;
  };
  toggleTitleState?: () => void;
  deleteHendler?: () => any;
}
type PropsInput = FieldRenderProps<string, any>;

const TextInput: React.FC<PropsInput> = ({
  input,
  meta,
  ...rest
}: PropsInput) => <input type="text" {...input} {...rest} />;

const Textinput = ({
  placeHolder,
  action,
  actionArguments,
  toggleTitleState,
  deleteHendler,
