import React, { useState } from "react";
import { Form, Field } from "react-final-form";
import { useDispatch, useSelector } from "react-redux";
import { addTodo } from "../../actions";
import { Input } from "../Input";
import { ItemInput } from "../Input";

interface Props {
  placeHolder: string;
  action: (...a: any) => { type: string; payload: string; id?: number };
  actionArguments?: {
    payload: string;
    id: number;
  };
  toggleTitleState?: () => void;
  deleteHendler?: () => any;
}
let state = { data: { title: "", titleItem: "" } };
const Textinput = ({
  placeHolder,
  action,
  actionArguments,
  toggleTitleState,
  deleteHendler,
}: Props) => {
  const dispatch = useDispatch();
  const todoList = useSelector((store: any) => store.todoList);
  const [inputTitle, setInputTitle] = useState("");
  // state = { data: { title: "...", titleItem: "item" } };
  const handelOnChange = (value: any) => {
    setInputTitle((prev) => prev + value);
    console.log(inputTitle);
  };
  const handelOnSubmit = (values: any) => {
    console.log("state", state);
    console.log("values", values);
    console.log(actionArguments ? actionArguments.payload : "---");

    // setInputTitle(values.title);
    // setInputTitle("");
    // state = { data: { title: "" } };

    if (values.title.trim() === "") {
      state = {
        data: {
          title: "...",
          titleItem: actionArguments ? actionArguments.payload : "+++",
        },
      };
    }

    if (actionArguments) {
      console.log("это тут", values.titleItem);

      dispatch(
        action(
          actionArguments ? actionArguments.payload : values.titleItem,
          actionArguments.id
        )
      );
    } else if (values.title.trim() !== "") {
      console.log("а не, тут");
      dispatch(addTodo(values.title));
    }

    if (toggleTitleState) toggleTitleState();

    if (values.title.trim() === "" || Object.keys(values).length === 0) {
      // if (inputTitle === "") {
      // if (deleteHendler) deleteHendler();
      return;
    }

    // Object.keys(values).length === 0
  };

  return (
    <div>
      <Form
        onSubmit={handelOnSubmit}
        initialValues={state.data}
        render={({ form, values }) => (
          <form
            onSubmit={(e: any) => (
              e.preventDefault(e),
              console.log(actionArguments),
              Object.keys(values).length !== 0 && handelOnSubmit(values),
              form.reset()
            )}
          >
            {!actionArguments ? (
              <Field<string>
                name="title"
                component={Input}
                placeholder={actionArguments ? inputTitle : placeHolder}
                onBlur={(e: any) => (
                  e.preventDefault(e),
                  console.log(actionArguments),
                  Object.keys(values).length !== 0 && handelOnSubmit(values),
                  form.reset()
                )}
              />
            ) : (
              <Field<string>
                name="titleItem"
                component={ItemInput}
                onChange={(val: any) => handelOnChange(val.nativeEvent.data)}
                placeholder={actionArguments ? "" : placeHolder}
                onBlur={(e: any) => (
                  e.preventDefault(e),
                  console.log(actionArguments),
                  Object.keys(values).length !== 0 && handelOnSubmit(values),
                  form.reset()
                )}
              />
            )}
          </form>
        )}
      />
    </div>
  );
};

export default Textinput;
