import React, { useCallback, memo } from "react";
import { connect } from "react-redux";
import { useDispatch } from "react-redux";

interface Props {
  dispatch: any;
  placeHolder: string;
  action: (...a: any) => { type: string; payload: string; id?: number };
  actionArguments?: {
    payload: string;
    id: number;
  };
  toggleTitleState?: () => void;
  deleteHendler?: () => any;
}

const InputMainComponent = ({
  dispatch,
  placeHolder,
  action,
  actionArguments,
  toggleTitleState,
  deleteHendler,
}: Props) => {
  const hookDispatck = useDispatch();

  let input: any;
  const initialvalu = actionArguments ? actionArguments.payload : "";

  const submitContent = useCallback((e) => {
    e.preventDefault();
    if (toggleTitleState) toggleTitleState();
    if (!input.value.trim()) {
      if (deleteHendler) deleteHendler();
      return;
    }
    actionArguments
      ? hookDispatck(action(input.value, actionArguments.id))
      : hookDispatck(action(input.value));

    input.value = "";
  }, []);

  return (
    <form onSubmit={submitContent}>
      <input
        placeholder={placeHolder}
        ref={(node) => {
          input = node;
          if (node) node.focus();
          if (node) node.value = initialvalu;
        }}
        onBlur={submitContent}
      ></input>
    </form>
  );
};
const Textinput = memo(InputMainComponent);
export default connect()(Textinput);
